package com.example.testing.Controller;

import android.app.SearchManager;
import android.content.Intent;
import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.appcompat.widget.SearchView;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.DividerItemDecoration;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.view.LayoutInflater;
import android.view.Menu;
import android.view.MenuInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.testing.Model.Book;
import com.example.testing.Model.BookAdapter;
import com.example.testing.R;

import java.util.ArrayList;
import java.util.List;

/**
 * A simple {@link Fragment} subclass.
 * Use the {@link SearchFragment#newInstance} factory method to
 * create an instance of this fragment.
 */
public class SearchFragment extends Fragment {

    // TODO: Rename parameter arguments, choose names that match
    // the fragment initialization parameters, e.g. ARG_ITEM_NUMBER
    private static final String ARG_PARAM1 = "param1";
    private static final String ARG_PARAM2 = "param2";

    // TODO: Rename and change types of parameters
    private String mParam1;
    private String mParam2;

    private RecyclerView rcvBookSearch;
    private BookAdapter bookAdapter;
    private View mView;

    private SearchView searchView;


    public SearchFragment() {
        // Required empty public constructor
    }

    /**
     * Use this factory method to create a new instance of
     * this fragment using the provided parameters.
     *
     * @param param1 Parameter 1.
     * @param param2 Parameter 2.
     * @return A new instance of fragment SearchFragment.
     */
    // TODO: Rename and change types and number of parameters
    public static SearchFragment newInstance(String param1, String param2) {
        SearchFragment fragment = new SearchFragment();
        Bundle args = new Bundle();
        args.putString(ARG_PARAM1, param1);
        args.putString(ARG_PARAM2, param2);
        fragment.setArguments(args);
        return fragment;
    }

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        if (getArguments() != null) {
            mParam1 = getArguments().getString(ARG_PARAM1);
            mParam2 = getArguments().getString(ARG_PARAM2);
        }
        setHasOptionsMenu(true);
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        mView = inflater.inflate(R.layout.fragment_search, container, false);

        rcvBookSearch = mView.findViewById(R.id.rcv_book_search);
        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(getActivity());
        rcvBookSearch.setLayoutManager(linearLayoutManager);

        rcvBookSearch.setVisibility(View.GONE);
        List<Book> bookList = getListBooks();

        bookAdapter = new BookAdapter(bookList);
        rcvBookSearch.setAdapter(bookAdapter);

        RecyclerView.ItemDecoration itemDecoration = new DividerItemDecoration(getActivity(), DividerItemDecoration.VERTICAL);
        rcvBookSearch.addItemDecoration(itemDecoration);

        bookAdapter.setOnBookClickListener(new BookAdapter.OnBookClickListener() {
            @Override
            public void onBookClick(Book book) {
                Intent intent = new Intent(getActivity(), DetailActivity.class);
                intent.putExtra("bookId", book.getBookId());
                startActivity(intent);
            }
        });

        return mView;
    }

    @Override
    public void onCreateOptionsMenu(@NonNull Menu menu, @NonNull MenuInflater inflater) {
        inflater.inflate(R.menu.search_menu, menu);
        SearchManager searchManager = (SearchManager) requireActivity().getSystemService(requireActivity().SEARCH_SERVICE);
        searchView = (SearchView) menu.findItem(R.id.action_search).getActionView();
        searchView.setSearchableInfo(searchManager.getSearchableInfo(requireActivity().getComponentName()));
        searchView.setMaxWidth(Integer.MAX_VALUE);

        searchView.setOnQueryTextListener(new SearchView.OnQueryTextListener() {
            @Override
            public boolean onQueryTextSubmit(String query) {
                // Không cần xử lý ở đây, trả về false để cho SearchView biết bạn đã xử lý tìm kiếm
                return false;
            }

            @Override
            public boolean onQueryTextChange(String newText) {
                if (bookAdapter != null) {
                    List<Book> filteredList = new ArrayList<>();
                    List<Book> list = getListBooks();
                    for (Book book : list) {
                        if (book.getTitle().toLowerCase().contains(newText.toLowerCase())) {
                            filteredList.add(book);
                        }
                    }

                    bookAdapter.setData(filteredList);

                    if (newText.isEmpty() || filteredList.isEmpty()) {
                        rcvBookSearch.setVisibility(View.GONE);
                    } else {
                        rcvBookSearch.setVisibility(View.VISIBLE);
                    }
                }
                return true;
            }
        });

        super.onCreateOptionsMenu(menu, inflater);
    }

    private List<Book> getListBooks() {
        List<Book> listBook = new ArrayList<>();

//        listBook.add(new Book(R.drawable.book1, "Book 1"));
//        listBook.add(new Book(R.drawable.book2, "Book 2"));
//        listBook.add(new Book(R.drawable.book3, "Book 3"));
//        listBook.add(new Book(R.drawable.book4, "Book 4"));
//        listBook.add(new Book(R.drawable.book5, "Book 5"));
        listBook.add(new Book(1,"", "Book 1", 5));
        listBook.add(new Book(2,"", "Book 2",1));
        listBook.add(new Book(3,"","Book 3", 5));
        listBook.add(new Book(4,"","Book 4", 5));
        listBook.add(new Book(5,"", "Book 5", 6));


        return listBook;
    }


}